import React, { useState } from "react"; //we have imported a hook called useState, between curly braces we import specific elements from react library here use
import "./ExpenseItem.css";
import ExpenseDate from "./ExpenseDate";
import Card from "../UI/Card";

function ExpenseItem(props) {
  //   Below we have used array destructuring we are also using useState hook over here,
  //   useState returns an array in which the first element is the initial state/value and second element is a
  //   state/value changing method
  //   any changes to the state/value specified in the parameter of useState will trigger the function/component to load again
  const [title, setTitle] = useState(props.title);
  const clickHandler = () => {
    setTitle("Title Changed");
  };
  return (
    <Card className="expense-item">
      <ExpenseDate date={props.date} />
      <div className="expense-item__description">
        <h2>{title}</h2>
        <div className="expense-item__price">Rs {props.amount}</div>
      </div>
    </Card>
  );
}
export default ExpenseItem;
